import React, { useState } from 'react';
import { Box, Button, Header, Avatar } from 'grommet';
import { Home, Book, Contact, Projects, Briefcase } from 'grommet-icons';
import { Link, useLocation} from "react-router-dom";

function AppBar() {
  const location = useLocation();
  const [home, setHome] = useState(true);
  const [education, setEducation] = useState(false);
  const [career, setCareer] = useState(false);
  const [portfolio, setPortfolio] = useState(false);
  const [contact, setContact] = useState(false);

  if (location.pathname === '/') {
    setHome(true);
    setEducation(false);
    setCareer(false);
    setPortfolio(false);
    setContact(false);
  } 

  if (location.pathname === '/education') {
    setHome(false);
    setEducation(true);
    setCareer(false);
    setPortfolio(false);
    setContact(false);
  } 

  if (location.pathname === '/career') {
    setHome(false);
    setEducation(false);
    setCareer(true);
    setPortfolio(false);
    setContact(false);
  } 

  if (location.pathname === '/portfolio') {
    setHome(false);
    setEducation(false);
    setCareer(false);
    setPortfolio(true);
    setContact(false);
  } 

  if (location.pathname === '/contact') {
    setHome(false);
    setEducation(false);
    setCareer(false);
    setPortfolio(false);
    setContact(true);
  } 

	return (
        <Header background="brand">
          <Box direction="row" pad={{left: 'medium'}} align='center'>
            <Avatar src="images/displaypic.png" />
          </Box>
          <Box direction = 'row' pad={{ right: 'medium', left: 'small', vertical: 'small' }}>
            <Link to="/">
              <Button icon={<Home />} active='${}'/>
            </Link>
            <Link to="/education">
              <Button icon={<Book />}/>
            </Link>
            <Link to="/career">
              <Button icon={<Briefcase />}/>
            </Link>
            <Link to="/portfolio">
              <Button icon={<Projects />} />
            </Link>
            <Link to="/contact">
              <Button icon={<Contact />} />
            </Link>
          </Box>
        </Header>
    );
};

export default AppBar;
