import React from 'react';
import { Box, Button, Heading, Grommet, Header, Paragraph, Avatar, Image, Text } from 'grommet';
import { Home, Book, Contact, Cli } from 'grommet-icons';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

//Import Components

import Portfolio from "./Portfolio";
import ContactPage from "./Contact";

const theme = {
  global: {
    colors: {
      brand: '#116466',
    },
    font: {
      family: 'Roboto',
      size: '18px',
      height: '20px',
    },
  },
};

function App() {

  function AppBar() {
    return (
      <Header background="brand">
        <Box direction="row" pad={{left: 'medium'}} align='center'>
          <Avatar src="images/displaypic.png" />
        </Box>
        <Box direction = 'row' pad={{ right: 'medium', left: 'small', vertical: 'small' }}>
          <Button icon={<Home />} onClick={() => {}} />
          <Button icon={<Book />} onClick="window.location.href='http://localhost:3000/portfolio'"/>
          <Button icon={<Contact />} onClick={() => {}} />
        </Box>
      </Header>
    );
  };

  function TechStackGrid() {
    return(
      <Box
      direction="row"
      pad="medium"
      >
        <Box pad="large" background="light-3" border={{ color: 'brand', size: 'small'}} >
          <Button icon={<Cli />} onClick={() => {}} />
        </Box>
        <Box pad="large" background="light-3" border={{ color: 'brand', size: 'small'}} >
        Test
        </Box>
      </Box>
    );
  };

  return (
    <Grommet theme={theme} full>
    <Router>
    <Box fill>
      <AppBar/>
      <Box direction='row' flex overflow={{ horizontal: 'hidden' }}>
        <Box flex align='center' justify='start' pad='medium'>
          <Heading level='2'>Hello, I'm Fuad, an aspiring Software Developer.</Heading>
          <Image src="images/displaypic.png" />
          <Heading level='3'>About</Heading>
          <Text>
            I'm a Technology Enthusiast who would love to contribute to a collective that values collaboration and diversity in the pursuit of success.
          </Text>
            Aside from work, I'm an football junkie, and also play Floorball in my free time.
          <Text>
            I graduated from the National University of Singapore (NUS) in 2020 with a Bachelors of Engineering (Hons) in Computer Engineering.
          </Text>
          <Text>
            I am also an incoming Technology Executive (SEED) at DBS.
          </Text>
          <Heading level='3'>Tech Stack</Heading>
          <TechStackGrid/>
        </Box>  
      </Box>
    </Box>
      <Route exact path="/portfolio" component={Portfolio} />
      <Route exact path="/contact" component={Contact} />
    </Router>
    </Grommet>
  );
}

export default App;
