import React from 'react';
import { Grommet, Box, Heading, Grid, Paragraph } from 'grommet';
import AppBar from './AppBar.js';

const theme = {
  global: {
    colors: {
      brand: '#116466',
    },
    font: {
      family: 'Roboto',
      size: '18px',
      height: '20px',
    },
  },
};

function Portfolio() {
	return (
    <Grommet theme={theme} full>
      <Box fill>
      <AppBar />
        <Box direction='row' flex overflow={{ horizontal: 'hidden' }}>
                <Box flex align='center' justify='start' pad='medium'>
                    <Heading level='2'>Portfolio</Heading>
                    <Grid
                      rows={['xxsmall', 'small']}
                      columns={['small', 'medium']}
                      gap="large"
                      areas={[
                        { name: 'nav', start: [0, 0], end: [0, 1] },
                        { name: 'heading', start: [0, 0], end: [1, 0] },
                        { name: 'main', start: [1, 1], end: [1, 1] },
                      ]}
                    >
                      <Box gridArea="nav" background="light-5" />
                      <Heading level='3' gridArea="heading" textAlign="center">
                      Mobile Vital Signs Monitoring System
                      </Heading>
                      <Paragraph gridArea="main">
                      System which incorporates bluetooth-enabled vital signs sensors communicating with microcontrollers which transfers data to a server, all done wirelessly. Enabling mobility in vital signs monitoring.
                      Technologies/Stack: CoffeeScript, Python, Elasticsearch, Logstash, Kibana, ZeroMQ
                      </Paragraph>
                    </Grid>
                    <Grid
                      rows={['xxsmall', 'small']}
                      columns={['small', 'medium']}
                      gap="large"
                      areas={[
                        { name: 'nav', start: [0, 0], end: [0, 1] },
                        { name: 'heading', start: [0, 0], end: [1, 0] },
                        { name: 'main', start: [1, 1], end: [1, 1] },
                      ]}
                    >
                      <Box gridArea="nav" background="light-5" />
                      <Heading level='3' gridArea="heading" textAlign="center">
                      HungryVery
                      </Heading>
                      <Paragraph gridArea="main">
                      Full-Stack Web Application. Food Delivery Service.
                      Technologies/Stack: PostgreSQL, Express.js, React.js, Node.js, JavaScript, HTML, CSS, SQL
                      </Paragraph>
                    </Grid>
                    <Grid
                      rows={['xxsmall', 'small']}
                      columns={['small', 'medium']}
                      gap="large"
                      areas={[
                        { name: 'nav', start: [0, 0], end: [0, 1] },
                        { name: 'heading', start: [0, 0], end: [1, 0] },
                        { name: 'main', start: [1, 1], end: [1, 1] },
                      ]}
                    >
                      <Box gridArea="nav" background="light-5" />
                      <Heading level='3' gridArea="heading" textAlign="center">
                      Dance Detection
                      </Heading>
                      <Paragraph gridArea="main">
                      Wearable dance detection device which can detect a dance move being performed by the wearer of the device. This was done using sensors, an Arduino Mega and a Raspberry Pi. We used several machine learning algorithms for the inference of the dances.
                      Technologies/Stack: Arduino, Raspberry Pi, Python, Machine Learning
                      </Paragraph>
                    </Grid>
                    <Grid
                      rows={['xxsmall', 'small']}
                      columns={['small', 'medium']}
                      gap="large"
                      areas={[
                        { name: 'nav', start: [0, 0], end: [0, 1] },
                        { name: 'heading', start: [0, 0], end: [1, 0] },
                        { name: 'main', start: [1, 1], end: [1, 1] },
                      ]}
                    >
                      <Box gridArea="nav" background="light-5" />
                      <Heading level='3' gridArea="heading" textAlign="center">
                      CollegeZone
                      </Heading>
                      <Paragraph gridArea="main">
                      CollegeZone is a custom application made for National University of Singapore (NUS) Residential College 4 (RC4) students. CollegeZone is designed for a RC4 student to manage their contacts with other RC4 students, manage their goals and to manage their time. You can find many interesting features available in CollegeZone such as: recording meetups, rating friends, creating goals, creating reminders and many more!
                      Technologies/Stack: Java, Travis CI/CD
                      </Paragraph>
                    </Grid>
                </Box>  
              </Box>
      </Box>
    </Grommet>
	);
}

export default Portfolio;
