import React from 'react';
import { Box, Button, Heading, Grommet } from 'grommet';
import { Home, Book, Contact } from 'grommet-icons';

const theme = {
  global: {
    colors: {
      brand: '#116466',
    },
    font: {
      family: 'Roboto',
      size: '18px',
      height: '20px',
    },
  },
};

function App() {

  function AppBar() {
    return (
      <Box
        tag='header'
        direction='row'
        align='center'
        justify='center'
        background='brand'
        pad={{ left: 'medium', right: 'small', vertical: 'small' }}
        elevation='medium'
        style={{ zIndex: '1' }}
      >
        Fuad
        <Button icon={<Home />} onClick={() => {}} />
        <Button icon={<Book />} onClick={() => {}} />
        <Button icon={<Contact />} onClick={() => {}} />
      </Box>
    );
  };

  return (
    <Grommet theme={theme} full>
    <Box fill>
      <AppBar/>
      <Box direction='row' flex overflow={{ horizontal: 'hidden' }}>
        <Box flex align='center' justify='center'>
        app body
      </Box>
      <Box
       width='medium'
       background='light-2'
       elevation='small'
       align='center'
       justify='center'
      >
       sidebar
      </Box>
      </Box>
    </Box>
    </Grommet>
  );
}

export default App;
