import React, { useState } from 'react';
import { Box, Button, Heading, Grommet, Header, Paragraph } from 'grommet';
import { Home, Book, Contact } from 'grommet-icons';

const theme = {
  global: {
    colors: {
      brand: '#116466',
    },
    font: {
      family: 'Roboto',
      size: '18px',
      height: '20px',
    },
  },
};

function App() {

  function AppBar() {
    return (
      <Header background="brand">
        <Box pad={{left: 'medium'}}>
          My Website
        </Box>
        <Box direction = 'row' pad={{ right: 'medium', right: 'small', vertical: 'small' }}>
        <Button icon={<Home />} onClick={() => {}} />
        <Button icon={<Book />} onClick={() => {}} />
        <Button icon={<Contact />} onClick={() => {}} />
        </Box>
      </Header>
    );
  };

  return (
    <Grommet theme={theme} full>
    <Box fill>
      <AppBar/>
      <Box direction='row' flex overflow={{ horizontal: 'hidden' }}>
        <Box flex align='center' justify='center' pad='medium'>
        <Heading level='2'>Software Developer, Technology Enthusiast, Tinkerer</Heading>
        <Paragraph>
          Hi, I'm Fuad and welcome to my website :)
        </Paragraph>
        </Box>
      </Box>
    </Box>
    </Grommet>
  );
}

export default App;
